import React, {useState} from 'react';

function Player(props) {
    const [showTeam, setShowTeam] = useState(false);
    let equipe = <></>
    if(showTeam) {
        equipe = <li>Equipe: {props.team}</li>
    }
    return (
        <>
            <li onClick="setShowTeam">Player: {props.name}</li>
            {equipe}
        </>
    )
}

class Players extends React.Component {
    players = []
    constructor(props) {
        super(props);
        this.players = [
            { name: 'Chris', team: 'Juve' },
            { name: 'Phil', team: 'FC Grasse' },
            { name: 'Vincent', team: 'Spartak Courbevoie' },
        ]
    }
    render() {
        const players = this.players.map(p => <Player key={p.name} name={p.name} team={p.team}/>);
        return (
            <>
                <h1>Liste des joueurs</h1>
                {players}
            </>)
    }
}

export default Players;
