            testUpper: false,
            testLower: false,
            testNumber: false
        };
    }
    render() {
        return (
            <>
                <div>
                    <label for="password">Mot de passe</label>
                    <input id="password" type="password" />
                </div>
                <div>
                    <p syle={{color:this.state.testLenght?'green':'red'}}>8 caract√®res minimum</p>
                    <p syle={{color:this.state.testUpper?'green':'red'}}>Une majuscule</p>
                    <p syle={{color:this.state.testLower?'green':'red'}}>Une minuscule</p>
                    <p syle={{color:this.state.testNumber?'green':'red'}}>Un chiffre</p>                    
                </div>
            </>)
    }
}

export default MotDePasse;
