import React from 'react';
import PlayerForm from './PlayerForm';
import PlayerList from './PlayerList';

class PlayerManager extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            players: []
        }
    }
    
    onSave(player) {
        console.log(player);
    }

    render() {
        return (<>
            <PlayerForm onSave={(p)=>this.onSave(p)}/>
            <PlayerList />
        </>)
    }
}

export default PlayerManager;
