import React, { useState } from 'react';
import Players from 'react';


function Test(props) {
    return (
        <div>
            <h1>Test</h1>
            <p>{props.content}</p>
        </div>
    )
}

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = { team: "Juventus" }
    }
    render() {
        return (
            <>
                <p>Player: {this.props.name}</p>
                <p>Equipe: {this.state.team}</p>
            </>
        )
    }
}

function MyApp() {
    //const title = "Simple titre";
    const [title, setTitle] = useState("Test");

    let list = <ul>
        <li>Element 1</li>
        <li>Element 2</li>
        <li>Element 3</li>
    </ul>

    return (
        <div>
            <button onClick={() => setTitle("Coucou")}>Click</button>
            <div>{title}</div>
            {list}
            <Test content="Contenu de mon composant" />
            <Player name="Philippe" />
        </div>
    );
}

export default MyApp;
