import React from 'react';

class PlayerForm extends React.Component {

    constructor(props) {
        super(props);
        console.log(props);
        this.state = {
            name: '',
            team: ''
        }
    }

    onSave() {
        console.log('onSave PlayerForm Component');
        this.props.onSave();
        this.state = {
            name: '',
            team: ''
        }
    }

    onHandleChange() {
        console.log("onHandleChange")
        this.state.name='test'
    }

    render() {
        return (
        <div>
            <input placeholder='Nom du joueur' value={this.state.name} onChange={this.onHandleChange()}/>
            <select value={this.state.team} onChange={this.onHandleChange()}>
                <option value="">- Choisir une Ã©quipe -</option>
                <option>Juve</option>
                <option>Madrid</option>
                <option>Strasbourg</option>
            </select>
            <button onClick={()=>this.onSave()}>Enregistrer</button>
        </div>
        )
    }
}

export default PlayerForm;
