import React, {Component, Children} from 'react';

var LangContext = React.createContext('fr');

class LangProvider extends Component {
    state = {
        lang: 'fr'
    }
    setLang = lang => {
        this.setState({lang})
    }
    render() {
        const { lang } = this.state
        const { setLang } = this
        return(
            <LangContext.Provider value={{lang, setLang}}>
                {Children}
            </LangContext.Provider>
        )
    }
}

