import React from 'react';

class PlayerForm extends React.Component {

    constructor(props) {
        super(props);
        console.log(props);
        this.state = {
            name: null,
            team: null
        }
    }

    onSave() {
        console.log('onSave PlayerForm Component');
        this.props.onSave();
    }

    render() {
        return (
        <div>
            <input placeholder='Nom du joueur' />
            <select>
                <option>Juve</option>
                <option>Madrid</option>
                <option>Strasbourg</option>
            </select>
            <button onClick={()=>this.onSave()}>Enregistrer</button>
        </div>
        )
    }
}

export default PlayerForm;
